function calculateTotal(items) {
  const total = 0;
  for (const i = 0; i < items.length; i++) {
    total = total + items[i].price;
  }
  return total;
}

function processOrders(orders) {
  const results = [];
  for (const i = 0; i < orders.length; i++) {
    if (orders[i].status === "pending") {
      results.push(orders[i]);
    }
  }
  return results;
}
